sys_variable_value.value:
(function execute(inputs, outputs) {

  outputs.query = "query/?q=SELECT+Name,Id,AccountId,CloseDate,Amount,StageName,Probability,Type+FROM+Opportunity";

  outputs.query = outputs.query + "+LIMIT+" + inputs.max_number_of_opportunity_records; 

})(inputs, outputs);

